var http = require('https');
var tar = require('tar');

var version = '4.0.0';
var lang = 'ocr-lang-eng';
var source = 'https://s3.amazonaws.com/accuproducts/ocrxpress/tessdata/' + lang + '-' + version + '.tar.gz';

console.log("Downloading language data...");
http.get(source, function(response) {
  response.pipe(tar.x()).on('finish', function () {
    console.log("Download complete!");
  });
});